mapper.select:
SELECT BBB.* FROM (
    SELECT ROW_NUMBER() OVER (ORDER BY num DESC) as rn, AAA.*
    FROM (SELECT num, title, writer, write_date, hits FROM sekki_center_board WHERE category=#{category}) AAA
) BBB
WHERE BBB.rn BETWEEN #{begin} AND #{end}

SELECT BBB.* FROM (
    SELECT ROW_NUMBER() OVER (ORDER BY num DESC) as rn, AAA.*
    FROM (SELECT num, title, writer, write_date, hits FROM sekki_center_board WHERE category=#{category} AND title LIKE CONCAT('%', #{search}, '%')) AAA
) BBB
WHERE BBB.rn BETWEEN #{begin} AND #{end}

mapper.update:
UPDATE sekki_center_board
SET title=#{title}, content=#{content}, files=#{files}, write_date=#{writeDate}, write_time=#{writeTime}, hits=0, likes=#{likes}
WHERE num=#{num}

mapper.select:
