    SELECT ROW_NUMBER() OVER (ORDER BY num DESC) as rn, AAA.*
    FROM (SELECT num, title, writer, write_date, hits, secret, replys FROM sekki_center_board WHERE category=#{category}) AAA
) BBB
WHERE BBB.rn BETWEEN #{begin} AND #{end}

SELECT BBB.* FROM (
