    FROM (SELECT num, title, writer, write_date, hits, secret, replys FROM sekki_center_board WHERE category=#{category} AND title LIKE CONCAT('%', #{search}, '%')) AAA
) BBB
WHERE BBB.rn BETWEEN #{begin} AND #{end}

